import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

function EventDetail() {
  const { eventId } = useParams();
  console.log(`eventId: ${eventId}`); // Log the eventId value

  const [event, setEvent] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!eventId) {
      setError('Event ID is required');
      setLoading(false);
      return;
    }

    axios.get(`http://localhost:8000/api/events/${eventId}/`)
      .then(response => {
        setEvent(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [eventId]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <h2>{event.name}</h2>
          <p><strong>Description:</strong> {event.description}</p>
          <p><strong>Date:</strong> {event.date}</p>
          <p><strong>Time:</strong> {event.time}</p>
          <p><strong>Location:</strong> {event.location}</p>
          <p><strong>Ticket Capacity:</strong> {event.ticket_capacity}</p>
          <p><strong>Available Tickets:</strong> {event.available_tickets}</p>
          <p><strong>Ticket Price:</strong> {event.ticket_price}</p>
          <Link to="/booking-form"><button>Make Booking</button></Link>
        </div>
      )}
    </div>
  );
}

export default EventDetail;
