import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import BookingForm from './BookingForm';

function EventDetail() {
  const { eventId } = useParams();
  const [event, setEvent] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState({}); // Assuming you have a way to get the current user's ID

  useEffect(() => {
    axios.get(`http://localhost:8000/api/events/${eventId}/`)
      .then(response => {
        setEvent(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [eventId]);

  useEffect(() => {
    // Assuming you have a way to get the current user's ID
    axios.get(`http://localhost:8000/api/users/me/`)
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <h2>{event.name}</h2>
          <p>{event.description}</p>
          <p>Available tickets: {event.available_tickets}</p>
          <BookingForm event={event} user={user} />
        </div>
      )}
    </div>
  );
}

export default EventDetail;
