import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EditEvent() {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    date: '',
    time: '',
    location: '',
    ticket_capacity: 0,
    ticket_price: 0.00,
  });

  useEffect(() => {
    axios.get(`http://localhost:8000/events/${id}/`)
      .then(response => setFormData(response.data))
      .catch(error => console.error('Error fetching event:', error));
  }, [id]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:8000/events/${id}/`, formData)
      .then(response => {
        console.log('Event updated:', response.data);
      })
      .catch(error => {
        console.error('Error updating event:', error);
      });
  };

  return (
    <div>
      <h1>Edit Event</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" value={formData.name} onChange={handleChange} />
        <input type="text" name="description" value={formData.description} onChange={handleChange} />
        <input type="date" name="date" value={formData.date} onChange={handleChange} />
        <input type="time" name="time" value={formData.time} onChange={handleChange} />
        <input type="text" name="location" value={formData.location} onChange={handleChange} />
        <input type="number" name="ticket_capacity" value={formData.ticket_capacity} onChange={handleChange} />
        <input type="number" name="ticket_price" value={formData.ticket_price} step="0.01" onChange={handleChange} />
        <button type="submit">Update Event</button>
      </form>
    </div>
  );
}

export default EditEvent;
