import { useState, useEffect } from 'react';
import useAxios from "../utils/useAxios";
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import { Link } from 'react-router-dom';
import UserBookings from '../components/UserBookings';

function Dashboard() {
  const [res, setRes] = useState("");
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('this_week'); // default filter
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [search, setSearch] = useState(''); // new search state
  const [searchBy, setSearchBy] = useState(''); // new searchBy state

  const api = useAxios();
  const token = localStorage.getItem("authTokens");

  let user_id, username, full_name, image;
  if (token) {
    const decode = jwtDecode(token);
    user_id = decode.user_id;
    username = decode.username;
    full_name = decode.full_name;
    image = decode.image;
  }

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const params = {};
        if (search) {
          if (searchBy === 'date') {
            params.date = search;
          } else if (searchBy === 'location') {
            params.location = search;
          } else if (searchBy === 'category') {
            params.category = search;
          }
        }
        const response = await axios.get(`http://localhost:8000/api/events/?filter=${filter}`, { params });
        setEvents(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };
    fetchEvents();
  }, [filter, search, searchBy]);

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleSearchByChange = (e) => {
    setSearchBy(e.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div>
      <div className="container-fluid" style={{ paddingTop: "100px" }}>
        <div className="row">
          <nav className="col-md-4 d-none d-md-block bg-light sidebar mt-4">
            <div className="sidebar-sticky">
              <ul className="nav flex-column mb-2">
                <li className="nav-item">
                  <UserBookings userId={user_id} />
                </li>
              </ul>
            </div>
          </nav>
          <main role="main" className="col-md-7 ml-sm-auto col-lg-8 pt-3 px-4">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pb-2 mb-3 border-bottom">
              <h1 className="h2">Hello {username}!</h1>
              <div className="btn-toolbar mb-2 mb-md-0">
                <div style={{ position: 'relative' }}>
                  <button
                    className="btn btn-sm btn-outline-secondary dropdown-toggle"
                    type="button"
                    id="filterDropdown"
                    onClick={toggleDropdown}
                  >
                    <span data-feather="calendar" />
                    {filter === 'this_week' ? 'This week' : filter === 'this_month' ? 'This month' : 'This year'}
                  </button>
                  <div
                    className={`dropdown-menu ${dropdownOpen ? 'show' : ''}`}
                    aria-labelledby="filterDropdown"
                    style={{
                      position: 'absolute',
                      top: '100%',
                      left: 0,
                      zIndex: 1000,
                      display: 'none',
                      float: 'left',
                      minWidth: '10rem',
                      padding: '0.5rem 0',
                      margin: '0.125rem 0 0',
                      fontSize: '1rem',
                      color: '#212529',
                      textAlign: 'left',
                      listStyle: 'none',
                      backgroundColor: '#fff',
                      backgroundClip: 'padding-box',
                      border: '1px solid rgba(0, 0, 0, 0.15)',
                      borderRadius: '0.25rem'
                    }}
                  >
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_week')}
                    >
                      This week </a>
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_month')}
                    >
                      This month
                    </a>
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_year')}
                    >
                      This year
                    </a>
                  </div>
                </div>
                <div className="input-group mb-3">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search events"
                    value={search}
                    onChange={handleSearchChange}
                  />
                  <div className="input-group-append">
                    <select
                      className="custom-select"
                      value={searchBy}
                      onChange={handleSearchByChange}
                    >
                      <option value="">All</option>
                      <option value="date">Date</option>
                      <option value="location">Location</option>
                      <option value="category">Category</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>
            <h2>Upcoming Events</h2>
            <div className="table-responsive">
              <table className="table table-striped table-sm">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Location</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody >
                  {loading ? (
                    <tr><td colSpan="3">Loading...</td></tr>
                  ) : error ? (
                    <tr><td colSpan="3">Error: {error}</td></tr>
                  ) : events.length ? (
                    events.map(event => (
                      <tr key={event.id}>
                        <Link to={`/events/${event.id}`}>
                            <td>{event.name}</td>
                        </Link>
                        <td>{event.location}</td>
                        <td>{event.date}</td>
                      </tr>
                    ))
                  ) : (
                    <tr><td colSpan="3">No events found</td></tr>
                  )}
                </tbody>
              </table>
            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
