import { useState, useEffect } from 'react';
import useAxios from "../utils/useAxios";
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import { Link } from 'react-router-dom';
import UserBookings from '../components/UserBookings';

function Dashboard() {
  const [res, setRes] = useState("");
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('this_week'); // default filter
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [dateFilter, setDateFilter] = useState(""); // For specific date filter
  const [monthFilter, setMonthFilter] = useState(""); // For month filter
  const [yearFilter, setYearFilter] = useState(""); // For year filter
  const [locationFilter, setLocationFilter] = useState(""); // For location filter

  const api = useAxios();
  const token = localStorage.getItem("authTokens");

  let user_id, username, full_name, image;
  if (token) {
    const decode = jwtDecode(token);
    user_id = decode.user_id;
    username = decode.username;
    full_name = decode.full_name;
    image = decode.image;
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await api.get("/test/");
        setRes(response.data.response);
      } catch (error) {
        console.log(error);
        setRes("Something went wrong");
      }
    };
    fetchData();
  }, [api]);

  // Fetch events in this useEffect
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/events/`, {
          params: {
            filter: filter,
            date: dateFilter,  // For specific date
            month: monthFilter, // For month
            year: yearFilter,   // For year
            location: locationFilter,
          },
        });
        setEvents(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };
    fetchEvents();
  }, [filter, dateFilter, monthFilter, yearFilter, locationFilter]); // Dependencies include filters

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const handleDateFilterChange = (event) => {
    setDateFilter(event.target.value);
  };

  const handleMonthFilterChange = (event) => {
    setMonthFilter(event.target.value);
  };

  const handleYearFilterChange = (event) => {
    setYearFilter(event.target.value);
  };

  const handleLocationFilterChange = (event) => {
    setLocationFilter(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div>
      <div className="container-fluid" style={{ paddingTop: "100px" }}>
        <div className="row">
          <nav className="col-md-4 d-none d-md-block bg-light sidebar mt-4">
            <div className="sidebar-sticky">
              <ul className="nav flex-column mb-2">
                <li className="nav-item">
                  <UserBookings userId={user_id} />
                </li>
              </ul>
            </div>
          </nav>
          <main role="main" className="col-md-7 ml-sm-auto col-lg-8 pt-3 px-4">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pb-2 mb-3 border-bottom">
              <h1 className="h2">Hello {username}!</h1>
              <div className="btn-toolbar mb-2 mb-md-0">
                <div style={{ position: 'relative' }}>
                  <button
                    className="btn btn-sm btn-outline-secondary dropdown-toggle"
                    type="button"
                    id="filterDropdown"
                    onClick={toggleDropdown}
                  >
                    <span data-feather="calendar" />
                    {filter === 'this_week' ? 'This week' : filter === 'this_month' ? 'This month' : 'This year'}
                  </button>
                  <div
                    className={`dropdown-menu ${dropdownOpen ? 'show' : ''}`}
                    aria-labelledby="filterDropdown"
                    style={{
                      position: 'absolute',
                      top: '100%',
                      left: 0,
                      zIndex: 1000,
                    }}
                  >
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_week')}
                    >
                      This week
                    </a>
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_month')}
                    >
                      This month
                    </a>
                    <a
                      className="dropdown-item"
                      href="#"
                      onClick={() => handleFilterChange('this_year')}
                    >
                      This year
                    </a>
                  </div>
                </div>
              </div>
            </div>

            <h2>Upcoming Events</h2>

            {/* Date, Month, Year, and Location Filters */}
            <div className="mb-3">
              <input
                type="date"
                value={dateFilter}
                onChange={handleDateFilterChange}
                className="form-control"
                placeholder="Filter by specific date"
              />
              <input
                type="month"
                value={monthFilter}
                onChange={handleMonthFilterChange}
                className="form-control mt-2"
                placeholder="Filter by month"
              />
              <input
                type="number"
                value={yearFilter}
                onChange={handleYearFilterChange}
                className="form-control mt-2"
                placeholder="Filter by year"
                min="2000" // You can set a minimum year based on your data
              />
              <input
                type="text"
                value={locationFilter}
                onChange={handleLocationFilterChange}
                className="form-control mt-2"
                placeholder="Filter by location"
              />
            </div>

            <div className="table-responsive">
              <table className="table table-striped table-sm">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Location</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {loading ? (
                    <tr><td colSpan="3">Loading...</td></tr>
                  ) : error ? (
                    <tr><td colSpan="3">Error: {error}</td></tr>
                  ) : events.length ? (
                    events.map(event => (
                      <tr key={event.id}>
                        <Link to={`/events/${event.id}`}>
                          <td>{event.name}</td>
                        </Link>
                        <td>{event.location}</td>
                        <td>{event.date}</td>
                      </tr>
                    ))
                  ) : (
                    <tr><td colSpan="3">No events found</td></tr>
                  )}
                </tbody>
              </table>
            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
