import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import BookingForm from './BookingForm';
import EventEditor from './EventEditor';

function EventDetail() {
  const { eventId } = useParams();
  console.log(`eventId: ${eventId}`); // Log the eventId value

  const [event, setEvent] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    if (!eventId) {
      setError('Event ID is required');
      setLoading(false);
      return;
    }

    axios.get(`http://localhost:8000/api/events/${eventId}/`)
      .then(response => {
        setEvent(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [eventId]);

  const handleUpdateEvent = (updatedEvent) => {
    // Call API or update event details logic here
    console.log('Event updated:', updatedEvent);
    setEvent(updatedEvent);
    setEditing(false);
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div>
          <h2>{event.name}</h2>
          <p>{event.description}</p>
          <p>Available tickets: {event.available_tickets}</p>
          <BookingForm event={event} />
          {editing ? (
            <EventEditor event={event} onSubmit={handleUpdateEvent} />
          ) : (
            <button onClick={() => setEditing(true)}>Edit Event</button>
          )}
        </div>
      )}
    </div>
  );
}

export default EventDetail;
