import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/user/bookings/', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('access_token')}`
          }
        });
        setBookings(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.response ? error.response.data : 'Error fetching bookings');
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Your Event Bookings</h2>
      {bookings && bookings.length === 0 ? (
        <p>You have no bookings.</p>
      ) : (
        <ul>
          {bookings.map(booking => (
            <li key={booking.id}>
              <h3>{booking.event.name}</h3>
              <p>Date: {new Date(booking.booking_date).toLocaleDateString()}</p>
              <p>Tickets: {booking.ticket_quantity}</p>
              <p>Total Cost: ${booking.total_cost}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserBookings;
