import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const UserBookings = ({ userId }) => {
  const [bookings, setBookings] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [bookingsForDate, setBookingsForDate] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8000/api/users/${userId}/bookings/`)
      .then(response => {
        setBookings(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, [userId]);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    const bookingsOnSelectedDate = bookings.filter(booking => new Date(booking.event_date).toDateString() === date.toDateString());
    setBookingsForDate(bookingsOnSelectedDate);
  };

  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const bookingsOnThisDate = bookings.filter(booking => new Date(booking.event_date).toDateString() === date.toDateString());
      if (bookingsOnThisDate.length > 0) {
        return <div className="dot"></div>; // Add a marker to indicate a booking exists on this date
      }
    }
  };

  return (
    <div>
      <h2>My Bookings</h2>
      <Calendar
        onClickDay={handleDateClick}
        tileContent={tileContent}
      />
      {selectedDate && bookingsForDate.length > 0 && (
        <div>
          <h3>Bookings on {selectedDate.toDateString()}</h3>
          <ul>
            {bookingsForDate.map(booking => (
              <li key={booking.id}>
                <p>Event: {booking.event_name}</p>
                <p>Location: {booking.event_location}</p>
                <p>Tickets: {booking.ticket_quantity}</p>
                <p>Total Cost: {booking.total_cost}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default UserBookings;
