import React, { useState } from 'react';
import axios from 'axios';

const CreateEvent = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [location, setLocation] = useState('');
  const [ticketCapacity, setTicketCapacity] = useState(0);
  const [ticketPrice, setTicketPrice] = useState(0.0);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('create/', {
        name,
        description,
        date,
        time,
        location,
        ticket_capacity: ticketCapacity,
        ticket_price: ticketPrice,
      });
      console.log(response.data);
      // Redirect to event list page
      window.location.href = '/event_list/';
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Create Event</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <br />
        <label>
          Description:
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </label>
        <br />
        <label>
          Date:
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        </label>
        <br />
        <label>
          Time:
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
        </label>
        <br />
        <label>
          Location:
          <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} />
        </label>
        <br />
        <label>
          Ticket Capacity:
          <input type="number" value={ticketCapacity} onChange={(e) => setTicketCapacity(e.target.value)} />
        </label>
        <br />
        <label>
          Ticket Price:
          <input type="number" value={ticketPrice} onChange={(e) => setTicketPrice(e.target.value)} />
        </label>
        <br />
        <button type="submit">Create Event</button>
      </form>
    </div>
  );
};

export default CreateEvent;
