import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function EventDetails() {
  const { id } = useParams(); // Get the event ID from the URL parameters
  const [event, setEvent] = useState(null); // State to hold event details
  const [loading, setLoading] = useState(true); // State to handle loading status

  useEffect(() => {
    // Fetch event details by ID when the component mounts
    axios.get(`http://localhost:8000/events/${id}/`)
      .then(response => {
        setEvent(response.data); // Set the event data
        setLoading(false); // Set loading to false
      })
      .catch(error => {
        console.error('Error fetching event details:', error);
        setLoading(false); // Handle loading failure
      });
  }, [id]);

  if (loading) {
    return <div>Loading event details...</div>; // Show loading message while fetching
  }

  if (!event) {
    return <div>Event not found!</div>; // If event data is not available
  }

  return (
    <div>
      <h1>{event.name}</h1>
      <p><strong>Description:</strong> {event.description}</p>
      <p><strong>Date:</strong> {event.date}</p>
      <p><strong>Time:</strong> {event.time}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Ticket Capacity:</strong> {event.ticket_capacity}</p>
      <p><strong>Available Tickets:</strong> {event.available_tickets}</p>
      <p><strong>Ticket Price:</strong> ${event.ticket_price}</p>
    </div>
  );
}

export default EventDetails;
