import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Or your preferred calendar library
import 'react-calendar/dist/Calendar.css'; // Import default styles
import './UserBookings.css'; // Import your custom styles

const UserBookings = () => {
  const [calendarBookings, setCalendarBookings] = useState([]); // Your booking data

  const handleDateClick = (date) => {
    // Handle date click logic here
  };

  return (
    <div className="user-bookings">
      <h2 className="user-bookings__title">My Bookings</h2>
      <Calendar
        onClickDay={handleDateClick}
        tileClassName={({ date, view }) => {
          if (view === 'month') {
            return calendarBookings.some(booking => 
              booking.date.toDateString() === date.toDateString()
            ) ? 'highlight' : null;
          }
        }}
        className="user-bookings__calendar" // Apply custom calendar styles
      />
    </div>
  );
};

export default UserBookings;
