import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const EventDetail = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');

  useEffect(() => {
    // Fetch event data from API or database using eventId
    const fetchEvent = async () => {
      const response = await fetch(`https://your-api.com/events/${eventId}`);
      const data = await response.json();
      setEvent(data);
      setTitle(data.title);
      setDescription(data.description);
      setDate(data.date);
    };
    fetchEvent();
  }, [eventId]);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call API or update event details logic here
    console.log('Event details updated:', { title, description, date });
  };

  if (!event) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Edit Event Details</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={handleTitleChange} />
        </label>
        <br />
        <label>
          Description:
          <textarea value={description} onChange={handleDescriptionChange} />
        </label>
        <br />
        <label>
          Date:
          <input type="date" value={date} onChange={handleDateChange} />
        </label>
        <br />
        <button type="submit">Update Event</button>
      </form>
    </div>
  );
};

export default EventDetail;
