import React, { useState } from 'react';

const EventEditor = ({ event, onSubmit }) => {
  const [title, setTitle] = useState(event.name || '');
  const [description, setDescription] = useState(event.description || '');
  const [date, setDate] = useState(event.date || '');
  const [time, setTime] = useState(event.time || '');
  const [location, setLocation] = useState(event.location || '');
  const [ticketCapacity, setTicketCapacity] = useState(event.ticket_capacity || '');
  const [availableTickets, setAvailableTickets] = useState(event.available_tickets || '');
  const [ticketPrice, setTicketPrice] = useState(event.ticket_price || '');
  const [numTicketsSold, setNumTicketsSold] = useState(event.num_tickets_sold || '');

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedEvent = {
      name: title,
      description,
      date,
      time,
      location,
      ticket_capacity: ticketCapacity,
      available_tickets: availableTickets,
      ticket_price: ticketPrice,
      num_tickets_sold: numTicketsSold,
    };
    onSubmit(updatedEvent);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Title:
        <input
          type="text"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        />
      </label>
      <br />
      <label>
        Description:
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
      </label>
      <br />
      <label>
        Date:
        <input
          type="date"
          value={date}
          onChange={(event) => setDate(event.target.value)}
        />
      </label>
      <br />
      <label>
        Time:
        <input
          type="time"
          value={time}
          onChange={(event) => setTime(event.target.value)}
        />
      </label>
      <br />
      <label>
        Location:
        <input
          type="text"
          value={location}
          onChange={(event) => setLocation(event.target.value)}
        />
      </label>
      <br />
      <label>
        Ticket Capacity:
        <input
          type="number"
          value={ticketCapacity}
          onChange={(event) => setTicketCapacity(event.target.value)}
        />
      </label>
      <br />
      <label>
        Available Tickets:
        <input
          type="number"
          value={availableTickets}
          onChange={(event) => setAvailableTickets(event.target.value)}
        />
      </label>
      <br />
      <label>
        Ticket Price:
        <input
          type="number"
          value={ticketPrice}
          onChange={(event) => setTicketPrice(event.target.value)}
        />
      </label>
      <br />
      <label>
        Number of Tickets Sold:
        <input
          type="number"
          value={numTicketsSold}
          onChange={(event) => setNumTicketsSold(event.target.value)}
        />
      </label>
      <br />
      <button type="submit">Update Event</button>
    </form>
  );
};

export default EventEditor;
