import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        // Get token from localStorage
        const token = localStorage.getItem('access_token');
        if (!token) {
          throw new Error('No token found. Please log in.');
        }

        // Fetch bookings from API
        const response = await axios.get('http://localhost:8000/api/user/bookings/', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Update state with bookings data
        setBookings(response.data);
        setLoading(false);
      } catch (error) {
        // Check if the error is due to an invalid or expired token
        if (error.response && error.response.status === 401) {
          setError('Unauthorized access. Please log in again.');
          localStorage.removeItem('access_token');  // Optionally clear invalid token
        } else {
          setError('Error fetching bookings');
        }
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  // Handle loading state
  if (loading) return <div>Loading...</div>;

  // Handle error state
  if (error) return <div>{error}</div>;

  // Render bookings
  return (
    <div>
      <h2>Your Event Bookings</h2>
      {bookings.length === 0 ? (
        <p>You have no bookings.</p>
      ) : (
        <ul>
          {bookings.map(booking => (
            <li key={booking.id}>
              <h3>{booking.event.name}</h3>
              <p>Date: {new Date(booking.booking_date).toLocaleDateString()}</p>
              <p>Tickets: {booking.ticket_quantity}</p>
              <p>Total Cost: ${booking.total_cost}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserBookings;
