import axios from 'axios';
import React, { useState } from 'react';

const CreateEvent = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [availableTickets, setAvailableTickets] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!title || !description || !date || !availableTickets) {
      setError('Please fill in all fields');
      return;
    }

    // Call API to create new event
    axios.post('http://localhost:8000/api/events/', {
      name: title,
      description,
      date,
      available_tickets: availableTickets,
    })
      .then(response => {
        console.log('Event created:', response.data);
        // Redirect to event detail page or display success message
      })
      .catch(error => {
        setError(error.message);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Title:
        <input
          type="text"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        />
      </label>
      <br />
      <label>
        Description:
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
      </label>
      <br />
      <label>
        Date:
        <input
          type="date"
          value={date}
          onChange={(event) => setDate(event.target.value)}
        />
      </label>
      <br />
      <label>
        Available Tickets:
        <input
          type="number"
          value={availableTickets}
          onChange={(event) => setAvailableTickets(event.target.value)}
        />
      </label>
      <br />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button type="submit">Create Event</button>
    </form>
  );
};

export default CreateEvent;
